$wmiObject = Get-WmiObject -Namespace ROOT\CIMV2 -Class Win32_SystemDriver;

$result = New-Object System.Collections.ArrayList;
$numerator = 0;

$wmiObject | ForEach-Object {
    $currentOutput = "" | Select-Object 'Display Name', Name, 'Path Name', Started, 'Start Mode', State, Status, A1_Key;
    $currentOutput.'Display Name' = $_.DisplayName;
    $currentOutput.Name = $_.Name;
    $currentOutput.'Path Name' = $_.PathName;
    $currentOutput.Started = $_.Started;
    $currentOutput.'Start Mode' = $_.StartMode;
    $currentOutput.State = $_.State;
    $currentOutput.Status = $_.Status;
    $currentOutput.A1_Key = [string]$numerator + ':\' + [string]($_.Name -replace ' ', '_');

    $result.Add($currentOutput) | Out-Null;
    $numerator = ($numerator + 1) 
}

$result;

